function ilspy(){
	sudo docker run --rm -v `pwd`:/samples cincan/ilspy /samples/"$1" > `basename $1`.cs
	echo `basename $1`.cs
}

function logcat(){
	adb logcat | grep -F "`adb shell ps | grep $1  | tr -s [:space:] ' ' | cut -d' ' -f2`"
}

function jwtnonefy(){
   header=$(echo "$1" | cut -d. -f1 | base64 -d | sed -E "s/"alg":"([^"]+)"/"alg":"none"/g" | base64 | sed 's/=//g');
   payload=$(echo "$1" | cut -d. -f 2);
   echo -e "
"$header.$payload.
}

function pull-apk(){
	pkg_name=$(adb shell pm list packages | cut -d: -f2 | grep $1)
	adb pull "$(adb shell pm path $pkg_name | cut -d: -f2 | grep base)"
	mv base.apk $pkg_name.apk &> /dev/null
}
